globals.transModName = function transModName(mod_name, param_name) {

  // Translate modulname
  switch (mod_name) {
	case 'Keyboard-Sequencer': mod_name = 'KEY-SEQ'; break;
	case 'Classic VCO': mod_name = 'VCO'; break;
	case 'Mixer with Sliders': mod_name = 'MIX'; break;
	case 'Hi Hat Free': mod_name = 'HI HAT'; break;
	case 'Soft Bell Free': mod_name = 'BELL'; break;
	case 'Snare Drum Free': mod_name = 'SNARE'; break;
	case 'Deep Free': mod_name = 'KICK'; break;
	case 'Bleak (Free)': mod_name = 'OSC'; break;
	case 'Slap (Free)': mod_name = 'ADSR'; break;
	case 'Stabile (Free)': mod_name = 'VCF'; break;
	case 'Tangents (Free)': mod_name = 'VCF'; break;
	case 'Knobs (Free)': mod_name = 'MIX'; break;
	case 'Debriatus (Free)': mod_name = 'DISTORT'; break;
  }

  // remove some module names  
  switch (mod_name) {
	case 'PatchMaster': 
	case 'MixMasterJr': 
	case 'Knob 5':
    mod_name = ''; 
    break;
  }

  // rule based
  if (mod_name.startsWith("Drums - ")) {
    mod_name = mod_name.replace('Drums -', '');
  }

  return mod_name;
}

globals.transParamName = function transParamName(mod_name, param_name) {

  // Translate parameternames
  switch (param_name) {
	case 'Transpose mode': param_name = 'TRANS'; break;
	case 'Wavetable position': param_name = 'WT POS'; break;
	case 'Frequency': param_name = 'FREQ'; break;
	case 'Cutoff frequency': param_name = 'CUTOFF'; break;
	case 'Cutoff frequency CV': param_name = 'EG'; break;
	case 'Cutoff Modulation': param_name = 'EG'; break;
	case 'Resonance': param_name = 'RES'; break;
	case 'Unison Voices': param_name = 'UNI'; break;
	case 'Unison Detune': param_name = 'DETUNE'; break;
	case 'Pre-Filter Gain': param_name = 'DRIVE'; break;
	case 'Mod 1 to Frequency': param_name = 'EG'; break;
	case 'Input 1 Level': param_name = 'Level 1'; break;
	case 'Input 2 Level': param_name = 'Level 2'; break;
	case 'Input 3 Level': param_name = 'Level 3'; break;
	case 'Level 1 (Knob 1)': param_name = 'Level 1'; break;
	case 'Level 2 (Knob 1)': param_name = 'Level 2'; break;
	case 'Level 3 (Knob 1)': param_name = 'Level 3'; break;
	case 'Level 1 (Knob 2)': param_name = 'Level 1'; break;
	case 'Level 2 (Knob 2)': param_name = 'Level 2'; break;
	case 'Level 3 (Knob 2)': param_name = 'Level 3'; break;
	case 'filter_freq': param_name = 'TONE'; break;
	case 'decay_1': param_name = 'DECAY CL'; break;
	case 'decay_2': param_name = 'DECAY OP'; break;
	case 'tune': param_name = 'TUNE'; break;
	case 'decay': param_name = 'DECAY'; break;
	case 'tune_env_int': param_name = 'TUNE INT'; break;
	case 'tune_decay': param_name = 'TUNE DECAY'; break;
	case 'drive': param_name = 'DRIVE'; break;
	case 'noise_balance': param_name = 'NOISE'; break;
	case 'noise_decay': param_name = 'NOISE DECAY'; break;
  }

  // Translate module dependend parameternames
	switch (mod_name) {
	case 'MixMasterJr': 
	  if (param_name.endsWith("level")) {
	    param_name = param_name.replace('level', '');
	  }
	  break;
	case 'Pulses': 
	  if (param_name.endsWith(" push")) {
	    param_name = param_name.replace(' push', '');
	    param_name = param_name.replace('Row ', '');
	  }
	  break;
	case 'Mutes': 
	  if (param_name.endsWith(" mute")) {
	    param_name = param_name.replace(' mute', '');
	    param_name = param_name.replace('Row ', '');
	  }
	  break;
	case 'MIX8': 
	case 'MIX4': 
	  if (param_name.startsWith("Channel ")) {
	    param_name = param_name.replace('Channel ', '');
	    param_name = param_name.replace('level', '');
	  }
	  break;
	}


  // rule based
  if (mod_name.startsWith("Drums - ")) {
    // Autodafe drums have only one knob
    param_name = '';
  }

  return param_name;
}

globals.sendButtonValue = function sendButtonValue(id, value) {
  var buType = getVar(id, "buType");
  // console.log("sendButtonValue id:"+id+"value:"+value+" buType:"+buType);
  if (buType != undefined && buType == "S4") {
    // buType S4 == send 1 0 1 0 for "button on" and "button off"
    if (value == 5 || value == 2) { 
      send('/button', {type: 'i', value: getProp(id, 'preArgs')}, {type: 'f', value: 1});
      setTimeout(() => { 
        send('/button', {type: 'i', value: getProp(id, 'preArgs')}, {type: 'f', value: 0}); 
        setTimeout(() => { 
          send('/button', {type: 'i', value: getProp(id, 'preArgs')}, {type: 'f', value: 1}); 
          setTimeout(() => { 
            send('/button', {type: 'i', value: getProp(id, 'preArgs')}, {type: 'f', value: 0}); 
          }, 110);
        }, 110);
      }, 110);
    }
  }
  else {
    // buType SD == send 1 for "button on" and 0 for "button off"
    if (value == 5) { 
      send('/button', {type: 'i', value: getProp(id, 'preArgs')}, {type: 'f', value: 1});
    }
    if (value == 2) { 
      send('/button', {type: 'i', value: getProp(id, 'preArgs')}, {type: 'f', value: 0});
    }
  }

  return "OK";
}


globals.getBuType = function getBuType(mod_name, param_name) {
  var buType = "SD";

  switch (mod_name) {
	case 'Keyboard-Sequencer': buType = 'S4'; break;
  }
  return buType;
}

