globals.handleTouchOnKnob = function handleTouchOnKnob(id, eventtype) {

  var autofade_value = getVar("root", "autofade_value");
  var autofade_duration = getVar("root", "autofade_duration");
  var autofade_times = getVar("root", "autofade_times");
  var autofade_wait = getVar("root", "autofade_wait");
  var joystick_learn = getVar("root", "joystick_learn");

  var fdr_num = getProp(id, 'preArgs');

  if (eventtype == "stop") {
    // console.log("handleTouchOnKnob: "+id+" joystick_learn="+joystick_learn+" autofade_value="+autofade_value) ;
    if (joystick_learn != undefined && joystick_learn != 'none') {
      setVar(joystick_learn, "fdr_num", fdr_num);
      setVar(joystick_learn, "label", get("knobLabel_"+fdr_num));
      setVar(joystick_learn, "label_org", get("knobLabel_"+fdr_num));
      set(joystick_learn, 0);
    }
    else if (autofade_value != undefined && autofade_value >= 0) {
      var start = Date.now();
      var fps = 30;
      var from = get(id);
      var to = autofade_value;
      var next_to = from; // target value for next loop

      // set duration for the range from - to ; tranform seconds in milliseconds
      autofade_duration = Math.abs(from - to) * (autofade_duration * 1000);
      autofade_wait = autofade_wait  * 1000;

      var autofade_looptime = autofade_duration;
      // console.log ("Autofade "+id+" start "+from+ " to "+autofade_value+ " in "+ autofade_duration+ " for "+autofade_times+" times with "+autofade_wait+" pause");
      setVar('knob_'+fdr_num, "padding", 2);
      var move = true;  // first loop = move-loop
      setInterval(()=>{
        var x = (Date.now() - start) / (move ? autofade_duration : autofade_wait);
        if (x < 1) {
          if (move) {
            var y = (to - from) * x + from;
            set('knob_'+fdr_num, y);
            // console.log("Autofade Set "+fdr_num+" y="+y);
          }
        }
        else {
          if (move) {
            autofade_times = autofade_times - 1;
            if (autofade_times == 0) {
              set('knob_'+fdr_num, to);
              setVar('knob_'+fdr_num, "padding", 7);
              // console.log("Autofade Done")
              clearInterval();
            }
            else {
              // next loop = wait-loop
              move = false;
              start = Date.now();
            }
          }
          else {
              // next loop = move-loop
            move = true;
            start = Date.now();
            from = get(id);
            to = next_to;
            next_to = from;
          }
        }
      }, 1000/fps);
    }
  }
  else if (eventtype == "start") {

    setVar('knob_fineAdjust',"fdr_num", fdr_num);
    setVar('knob_fineAdjust',"colorW", getProp(id, 'colorWidget'));
    set('knob_fineAdjust', get(id));
    set('knobLabel_fineAdjust', get('knobLabel_'+fdr_num));
  }    
  return "OK";
}

globals.handleKeySeqButton = function handleKeySeqButton(id) {
  var button_names = ["play", "prev", "rest", "next", "tie", "del", "clear", "insert","res", "select", "copy", "trans"];
  var keyseq_wait = getVar("root", "keyseq_wait");
  // console.log("handleKeySeqButton:"+id+ " keyseq_wait="+keyseq_wait);
  if (keyseq_wait != undefined && keyseq_wait == true) {
    setVar("root", "keyseq_wait", false);
    for (var i = 0; i < 12; i++) {
      var bid = "button_"+button_names[i];
      setVar(bid, "colorWidget", getVar(bid, "colorWidget_org"));
      setVar(bid, "label", getVar(bid, "label_org"));
    }
  }
