    setVar("root", "keyseq_wait", true);
    for (var i2 = 0; i2 < 12; i2++) {
      var bid2 = "button_"+button_names[i2];
      setVar(bid2, "colorWidget_org", getVar(bid2, "colorWidget"));
      setVar(bid2, "colorWidget", i2 < 11 ? "#c3b200" : "#882fc8");
      setVar(bid2, "label_org", getVar(bid2, "label"));
      setVar(bid2, "label", i2<10 ? "s0"+i2 : "s"+i2);
    }
  }
}

globals.handleTrowaButton = function handleTrowaButton(stepnum, value) {
  // switch to the channel of the button
  var ch = getProp("parent", "variables")["ch"];
  var activeChannel = get("switch_trowa_channel");
  //console.log("ch="+ch+" activeChannel="+activeChannel);
  if (ch != activeChannel) {
    set("switch_trowa_channel", ch);
  }
  // send the value to vcv
  send('127.0.0.1:7001', '/tsseq/edit/step/'+stepnum, value);
}

globals.distributeTrowaStep = function distributeTrowaStep(stepnum, value) {
  // set the (incomming) value on the button of the active channel
  var activeChannel = get("switch_trowa_channel");
  set("button_trowa_"+(stepnum+activeChannel*100), value);
}


globals.getTrowaUpdateInterval = function getTrowaUpdateInterval() {
