}


globals.changeTrowaPattern = function changeTrowaPattern(value) {
  // console.log("changeTrowaPattern "+value);

  // set play pattern via OSC'ELOT !!! Reason: set it synchronizes with the clock
  var pattvalue = (value-1) * (1/63) + 0.001;
  send('/fader', {type: 'i', value: 691}, {type: 'f', value: pattvalue});

  // set edit pattern too
  send('127.0.0.1:7001', '/tsseq/edit/pat', value);

  //
  // refeseh the channels of this pattern
  //
  var activeChannel = get("switch_trowa_channel");

  var millisPerStep = 100;
  // increase, when problems in refereshing for slow JS devices, decrease for fast devices

  var ch = 0;
  var interval = setInterval(function() {
    ch+=1;
    if (ch <= 8) {
      // console.log("refresh ch="+ch);
      set("switch_trowa_channel", ch);
    }
    else {
      // console.log("refresh channels done");
      set("switch_trowa_channel", activeChannel);
      clearInterval(interval); 
    }
  }, millisPerStep);
}

