globals.getOctNumber = function getOctNumber(midival) {
  var baseval = midival % 12;
  var octval = (midival-baseval) / 12 - 1;
  return octval;
}

globals.getNoteName = function getNoteName(midival) {
  var baseval = midival % 12;
  var octval = (midival-baseval) / 12 - 1;
  var noteName = "C";
  switch(baseval) {
    case 0: noteName = "C"; break;
    case 1: noteName = "C#"; break;
    case 2: noteName = "D"; break;
    case 3: noteName = "D#"; break;
    case 4: noteName = "E"; break;
    case 5: noteName = "F"; break;
    case 6: noteName = "F#"; break;
    case 7: noteName = "G"; break;
    case 8: noteName = "G#"; break;
    case 9: noteName = "A"; break;
    case 10: noteName = "A#"; break;
    case 11: noteName = "B"; break;
  }
  return noteName+octval;
}

globals.updateNoteButtons = function updateNoteButtons(addOct, addKeynote, scale) {
  var oct = getVar("panel_keyboard_grid", "oct");
  if (oct == undefined) oct = 3;
  oct = oct + addOct;
  if (oct < 0) {
    oct = 5;
  }
  if (oct > 5) {
    oct = 0;
  }

  var keynote = getVar("panel_keyboard_grid", "keynote");
  if (keynote == undefined) keynote = 0;
  keynote = keynote + addKeynote;
  if (keynote < 0) {
    keynote = 11;
  }
  if (keynote > 11) {
    keynote = 0;
  }

  if (scale == undefined) {
    scale = get("menu_scale");
  }
  setVar("panel_keyboard_grid", "oct", oct);
  setVar("panel_keyboard_grid", "keynote", keynote);

  // console.log("scale="+scale);
  var startNote = (oct + 1) * 12 + keynote;
  var scaleAdds = [2,2,1,2,2,2,1];
  switch (scale) {
    case 0: scaleAdds = [2,2,1,2,2,2,1]; break; // major
    case 1: scaleAdds = [2,1,2,2,1,2,2]; break; // minor
    case 2: scaleAdds = [2,2,3,2,3]; break; // major pentatonic
    case 3: scaleAdds = [3,2,2,3,2]; break; // minor pentatonic
    case 4: scaleAdds = [3,2,1,1,3,2]; break; // blues
    case 5: scaleAdds = [1,3,1,1,2,3,1]; break; // persian
    case 6: scaleAdds = [1,2,2,2,1,2,2]; break; // phrygian
  }
  var notevalue = startNote;
  for (var po = 1; po <= 4; po++) { //noprotect
    for (var bn = 1; bn <= 7; bn++) { //noprotect
      var buttonName = "button_note_"+po+bn;
      // console.log("set buttonName="+buttonName+ "notevalue="+notevalue);
      var add = scaleAdds[bn-1];
      if (add == undefined) {
        setVar(buttonName, "notevalue", 0);
        setVar(buttonName, "label", "")
        setVar(buttonName, "fillOff", 0);
      }
      else {
        setVar(buttonName, "notevalue", notevalue);
        setVar(buttonName, "label", globals.getNoteName(notevalue)); //+"\n"+notevalue);
        setVar(buttonName, "fillOff", globals.getOctNumber(notevalue)*0.1);
        notevalue = notevalue + add;
      }
    }
  }
}

globals.markSuggests = function markSuggests(id) {
  if (false) { // disabled for now
    var thisNotevalue = getVar(id, "notevalue");
    for (var po = 1; po <= 4; po++) { //noprotect
      for (var bn = 1; bn <= 7; bn++) { //noprotect
        var buttonName = "button_note_"+po+bn;
        var otherNotevalue = getVar(buttonName, "notevalue");
        var diff = Math.abs(otherNotevalue - thisNotevalue) % 12;
        switch (diff) {
          case 0:
          case 12:
            setVar(buttonName, "color", "#0030fd");
            break;
          case 7:
            setVar(buttonName, "color", "#72b0fb");
            break;
          case 5:
          case 9:
            setVar(buttonName, "color", "#00f2fd");
            break;
          case 4:
          case 8:
            setVar(buttonName, "color", "#15fd00");
            break;
          case 3:
          case 10:
            setVar(buttonName, "color", "#fde700");
            break;
          case 2:
          case 11:
            setVar(buttonName, "color", "#fd9c00");
            break;
          case 6:
          case 1:
            setVar(buttonName, "color", "#fd3c00");
            break;
        }
      }
    }
  }
}

